class Solution {
public:
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        int n = rows*cols;
        int left=cStart, right=cStart+1, bottom=rStart+1,top=rStart;
        vector<vector<int>> ans;
        while(n){
            //right
            for(int i=left; i<=right; i++){
                if(i<=cols-1 && i>=0 && top>=0 && top<=rows-1){
                    ans.push_back({top, i});
                    n--;
                }
            }
            left--;

            //bottom
            for(int i=top+1; i<=bottom; i++){
                if(right<=cols-1 && right>=0 && i>=0 && i<=rows-1){
