class Solution {
    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        int len = rows*cols;
        int[][] ans = new int[len][2];
        int ai = 0;
        ans[ai][0] = rStart;
        ans[ai][1] = cStart++;
        ai++;
        int rEnd = rStart-1;
        int cEnd = cStart;
        while(ai<len){
            //go right
            for(int i=cStart; i<=cEnd && ai<len; i++){
                if(rStart<0 || i<0  || i>=cols) continue;
                ans[ai][0] = rStart;
                ans[ai][1] = i;
                ai++;
            }
            rStart++; //cuz starting row is covered now
            rEnd+=2;

            //go down -> travel rows
            for(int i=rStart; i<=rEnd && ai<len; i++){
                if(i<0 || i>=rows || cEnd>=cols) continue;
                ans[ai][0] = i;
                ans[ai][1] = cEnd;
                ai++;
            }
            cEnd--; //cuz last column is finished now
            cStart-=2;

            //go left -> travel columns
            for(int i=cEnd; i>=cStart && ai<len; i--){
                if(rEnd>=rows || i<0 || i>=cols) continue;
                ans[ai][0] = rEnd;
                ans[ai][1] = i;
                ai++;
            }
            rEnd--; //cuz last row is now done
            rStart-=2;

            //go up -> travel rows
            for(int i=rEnd; i>=rStart && ai<len; i--){
                if(cStart<0 || i<0 || i>=rows) continue;
                ans[ai][0] = i;
                ans[ai][1] = cStart;
                ai++;
            }
            cStart++; //cuz starting col is now done
            cEnd+=2;
        }
        return ans;
    }
}

