        int y = cStart;
        int offset = Math.max(cStart, cols - cStart);
        int rightLimit = cStart + 2, downLimit = rStart + 2, leftLimit = cStart - 1, upLimit = rStart - 1;
        while(count <= rows * cols) {
            // right
            while(y < rightLimit) {
                if(x >= 0 && x < rows && y >= 0 && y < cols) {
                    spiralMatrix[count - 1][0] = x;
                    spiralMatrix[count - 1][1] = y;
                    count++;
                }
                if(y <= rightLimit - 1) y++;
            }
            y--;

            // down
            x++;
            while(x < downLimit) {
                if(x >= 0 && x < rows && y >= 0 && y < cols) {
                    spiralMatrix[count - 1][0] = x;
                    spiralMatrix[count - 1][1] = y;
                    count++;
                }
                if(x <= downLimit - 1) x++;
            }
            x--;

            // left
            y--;
            while(y >= leftLimit) {
                if(x >= 0 && x < rows && y >= 0 && y < cols) {
                    spiralMatrix[count - 1][0] = x;
                    spiralMatrix[count - 1][1] = y;
                    count++;
                }
                if(y >= leftLimit) y--;
            }
            y++;

            // up
