class Solution {
public:
    vector<int> numOfBurgers(int tomatoSlices, int cheeseSlices) {
        // Binary search: we know for sure the # of small burgers will be >= 0 && <= cheeseSlices.
        // So we use binary search to find out the ultimate # of small burgers.
        //
        // The condition is:
        // 1) if the left tomato slices and cheese slices can make exact N # of jumbos, return the result.
        // 2) if the left tomato slices is not enough to make exact # of jumbos, we need to make more small
        //    burgers and less jumbo burgers, hence search from mid + 1 to right.
        // 3) if left cheese slices is not enough to make exact # of jumbos, we need to make less small burgers
        //    and more jumbo burgers, hence search from left to mid - 1.
        return numOfBurgers(tomatoSlices, cheeseSlices, 0, cheeseSlices);
    }

private:
    vector<int> numOfBurgers(int tomatoSlices, int cheeseSlices, int left, int right)
    {
        if (left > right)
        {
            return {};
        }

        int mid = left + (right - left) / 2; 
        int leftTomatoes = tomatoSlices - mid * 2;
        int leftCheeses = cheeseSlices - mid;

        if (leftCheeses * 4 == leftTomatoes)
        {
            return {leftCheeses, mid};
        }

        if (leftCheeses * 4 < leftTomatoes)
        {
            return numOfBurgers(tomatoSlices, cheeseSlices, left, mid - 1);
        }

        return numOfBurgers(tomatoSlices, cheeseSlices, mid + 1, right);
    }
};
```

