public:
    vector<int> numOfBurgers(int tomatoSlices, int cheeseSlices) {
        vector<int> ans;
        if (cheeseSlices > tomatoSlices) return ans;

        float jb= (tomatoSlices- 2*cheeseSlices)/2.0;
        if (jb == ceil(jb) && jb>=0){
            ans.push_back(int(jb));
        }
        else {
            return {};
        }

        float sb= (4*cheeseSlices - tomatoSlices)/2.0;
        if (sb == ceil(sb) && sb>=0 ){
            ans.push_back(int(sb));
            return ans;
        }
        else {
            return {};
        }
    }
};
