class Solution {
public:
    vector<int> numOfBurgers(int tomatoSlices, int cheeseSlices) {
        int ch=cheeseSlices;
        int to=tomatoSlices;
        if(ch>to || ch*4<to) return {};

        long long int x=to/2-ch;
        long long int y=ch-x;
        if(x<0 || y<0) return {};

         if(x+y==ch && ((x*4+y*2)==to)) return {(int)x,(int)y};
         return {};
    }
};
```

