class Solution {
    public List<Integer> numOfBurgers(int toma, int cheese) {
        List<Integer> res = new ArrayList<>();

        int small_cheese = cheese * 2;  /// use up all the cheese to make small burgers
        int total_big = 0;
        int total_small = 0;
        if (toma >= cheese * 2 && toma <= 4 * cheese && toma % 2 == 0) {
 // we divide by 2 instead of 4 because 2 cheese are accounted for in the 'small' burger
            total_big = (toma - small_cheese) /2;  
            total_small = cheese - total_big;
        }
        else 
            return new ArrayList<>();
        res.add(total_big);  res.add(total_small);
        return res;
    }



}
