func numOfBurgers(tomatoSlices int, cheeseSlices int) []int {
    reminder := (4*cheeseSlices - tomatoSlices) % 2
    if reminder != 0 {
        return nil
    }

    small := (4*cheeseSlices - tomatoSlices) / 2
    jumbo := cheeseSlices - small

    if small < 0 || jumbo < 0 {
        return nil
    }
    return []int{jumbo, small}
}
