class Solution {
public:
    vector<int> numOfBurgers(int T, int C) {
        vector<int>res;
        if (T % 2 == 0 && T >= C){
            int x = (T - 2*C)/2;
            int y = C - x;
            if (x < 0 || y < 0)return res; // count can't be negatives
            res.push_back(x);
            res.push_back(y);
        }
        return res;
    }
};
