        x_ref = self.find(x, float('inf'), parent, weight)
        y_ref = self.find(y, float('inf'), parent, weight)
        if x_ref != y_ref:
            if rank[x_ref] < rank[y_ref]:
                parent[x_ref] = y_ref
                weight[x_ref] = limit
            else:
                parent[y_ref] = x_ref
                weight[y_ref] = limit
