// Approach 1: Depth First Search - TLE

// Time complexity: O(n\xB2)
// Space complexity: O(n\xB2)	

class Solution {
    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
        Map<Integer, List<Pair<Integer, Integer>>> adj = new HashMap<>();

        for (int[] edge : edgeList) {
            adj.computeIfAbsent(edge[0], k -> new ArrayList<>()).add(new Pair(edge[1], edge[2]));
            adj.computeIfAbsent(edge[1], k -> new ArrayList<>()).add(new Pair(edge[0], edge[2]));
        }

        boolean[] answer = new boolean[queries.length];
        int i = 0;

        for (int[] query : queries) {
            boolean[] visited = new boolean[n];
            answer[i++] = dfs(adj, query[0], query[1], query[2], visited); 
        }

        return answer;
    }

    private boolean dfs(Map<Integer, List<Pair<Integer, Integer>>> adj, int curr, int dest, int limit, boolean[] visited) {
        if (curr == dest)
            return true;

        visited[curr] = true;

        if (!adj.containsKey(curr))
            return false;

        for (Pair<Integer, Integer> next : adj.get(curr)) {
            if (!visited[next.getKey()] && next.getValue() < limit) {
                if (dfs(adj, next.getKey(), dest, limit, visited))
                    return true;
            }
        }

        return false;
    }
}
