class Solution {
public:

    // darna mt bhai


   /* create a mst by given graph using krushkal's algorithm once mst is formed then we use binary lifting concept for finding lowest common ansestor is used for answering the query

 along with storing the parent array we also maintain a maxweight array 
 where maxweight[i][j] tell that what is the maximum weight in path between 
 node i and node (i+2^j) 

 this can be calculated using dfs and once these vales are calculated then
 using lowest common anscestor method we can answer the query

 for better understanding look at my submission

 */
    // custom comparator used for sorting the edges array 
    // helpful in kruskal's algorithm
    static bool comp(vector<int>&a,vector<int>&b){
        return a[2]<b[2];
    }

    // x=30 used for binary lifting
    int x=30;

    // different vectors used 
    vector<vector<int>>parent,maxweight;
    vector<int>par,Size,vis,depth;
    vector<vector<pair<int,int>>>g;

// union find to create mst
    void make(int i){
        par[i]=i;
        Size[i]=1;
    }
    int find(int a){
        if(par[a]==a)return a;
        return par[a]=find(par[a]);
    }
    void Union(int a,int b){
        a=find(a),b=find(b);
        if(a!=b){
            if(Size[a]<Size[b])swap(a,b);
            par[b]=a;
            Size[a]+=Size[b];
            return;
        }
    }

    //creating mst using kruskal's algorithm

    void create_mst(vector<vector<int>>&edge,int n){
        sort(edge.begin(),edge.end(),comp);
        par.clear(),Size.clear();
        par.resize(n),Size.resize(n);
        for(int i=0;i<n;++i)make(i);
        g.clear(),g.resize(n);
        for(auto e:edge){
            int a=e[0],b=e[1],w=e[2];
            if(find(a)!=find(b)){
                Union(a,b);
                g[a].push_back({b,w});
                g[b].push_back({a,w});
            }
        }
        return;
    }

    //run dfs for calculating (initial values of parent and maxweight)

    void dfs(int i){
        vis[i]=true;
        for(auto ch:g[i]){
            int v=ch.first,w=ch.second;
            if(!vis[v]){
                parent[v][0]=i;
                depth[v]=depth[i]+1;
                maxweight[v][0]=w;
                dfs(v);
            }
        }

    }

    // this function calculating all parent and maxweight values
    void cal(int n){
     parent.clear(),maxweight.clear(),depth.clear(),depth.resize(n,0);
parent.resize(n,vector<int>(x+1,-1)),maxweight.resize(n,vector<int>(x+1,INT_MIN));
        vis.clear();
        vis.resize(n,false);
        for(int i=0;i<n;++i){
            if(!vis[i]){
                depth[i]=0;
                dfs(i);
            }
        }

        //calculating rest of parent and maxweight values

        for(int j=1;j<=x;++j){
            for(int i=0;i<n;++i){
              if(parent[i][j-1]!=-1){
    parent[i][j]=parent[parent[i][j-1]][j-1];
    maxweight[i][j]=max(maxweight[i][j-1],maxweight[parent[i][j-1]][j-1]);
              }
            }
        }
    }

    // modified method for finding k_Th ancestor

    pair<int,int> k_th_ancestor(int a,int k){
        int weight=INT_MIN;
        for(int i=x;i>=0;--i){
            if(k&(1<<i)){
                k-=(1<<i);
                weight=max(weight,maxweight[a][i]);
                 a=parent[a][i];
                if(a==-1)return {-1,INT_MIN};
            }
        }
        return {a,weight};
    }

    //function for finding lca
    int lca(int a,int b){
        if(depth[a]>depth[b])swap(a,b);
        int d=depth[b]-depth[a];
        auto pr=k_th_ancestor(b,d);
        b=pr.first;
        int weight=pr.second;
        if(a==b)return weight;
        for(int i=x;i>=0;--i){
            if(parent[a][i]!=parent[b][i]){
                weight=max(weight,max(maxweight[a][i],maxweight[b][i]));
                a=parent[a][i];
                b=parent[b][i];
            }
        }
        weight=max(weight,max(maxweight[a][0],maxweight[b][0]));
        return weight;
    }

    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        create_mst(edgeList,n);
        cal(n);
        vector<bool>ans(queries.size());
        for(int i=0;i<queries.size();++i){
            int a=queries[i][0],b=queries[i][1],limit=queries[i][2];
            if(find(a)!=find(b)){
                ans[i]=false;
                continue;
            }
            ans[i]=(lca(a,b)<limit);
        }
        return ans;
    }
};
