    class DSU{
        vector<int> par,rank;
        public:

        DSU(int n){
            par.resize(n);
            rank.resize(n,1);
            iota(begin(par),end(par),0);
        }

        int find(int u){
            if(par[u]==u) return u;
            else return par[u]= find(par[u]);
        }

        void union_p(int u,int v){
            u= find(u);
            v= find(v);

            if(rank[u]<rank[v]) par[u]=v;
            else if(rank[u]>rank[v]) par[v]=u;
            else{
                par[v]=u;
                rank[u]++;
            }
        }
