class Solution {
        public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
            int[] parent = new int[n];

            for (int i = 0; i < n; ++i) {
                parent[i] = i;
            }

            // Sort the edges in the provided graph based on distance.
            Arrays.sort(edgeList, Comparator.comparing(a -> Integer.valueOf(a[2])));

            // Sort queries in increasing order of distance.
            List<int[]> q = new ArrayList<>();
            for (int i = 0; i < queries.length; ++i) {
                int[] entry = new int[4];
                entry[0] = queries[i][0]; // Start index
                entry[1] = queries[i][1]; // End Index
                entry[2] = queries[i][2]; // Threshold
                entry[3] = i; //

                q.add(entry);
            }

            q.sort(Comparator.comparing(a -> a[2]));

            int index = 0;
            boolean[] result = new boolean[q.size()];
            for (var entry : q) {
                int threshold = entry[2];
                //
                while (index < edgeList.length && edgeList[index][2] < threshold) {
                    union(parent, edgeList[index][0], edgeList[index][1]);
                    ++index;
                }

                result[entry[3]] = find(parent, entry[0]) == find(parent, entry[1]);
            }

            return result;
        }

        // O(Log(N))
        int find(int[] parent, int ind) {
            if (parent[ind] != ind) {
                return find(parent, parent[ind]);
            }

            return ind;
        }

        // O(Log(N))
        void union(int[] parent, int x, int y) {
            int p1 = find(parent, x);
            int p2 = find(parent, y);
            parent[p1] = p2;
        }
    }
