class Solution {
    private static class DisJointSet{
        int val;
        DisJointSet par;
        public DisJointSet(int value){
            this.val = value;
            this.par = this;
        }
    }

    private void union(DisJointSet set1, DisJointSet set2){ set1.par = set2; }

    private DisJointSet find(DisJointSet set){
        if(set.par == set) return set;
        return set.par = find(set.par);
    }

    private HashMap<Integer, DisJointSet> hashMap = new HashMap<>();
    private HashMap<DisJointSet, Set<Integer>> nodeMap = new HashMap();
    private int nodes;

    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
        nodes = n;
        for (int i = 0; i < nodes; i++)
            hashMap.put(i, new DisJointSet(i));
        //sort edges by weight - this is trivial thing to begin Kruskal
        Arrays.sort(edgeList, Comparator.comparingInt(arr -> arr[2]));

        //mark index along with query - not a better way as it use space ---use hashmap or something
        int[][] queriesWithIdx = new int[queries.length][];
        for (int idx = 0; idx < queries.length; idx++) {
            int[] query = queries[idx];
            int[] nquery = new int[]{query[0], query[1], query[2],idx};
            queriesWithIdx[idx] = nquery;
        }

        //sort queries by cost so that we will use edges only till that cost threshold to build MST
        Arrays.sort(queriesWithIdx, Comparator.comparingInt(arr -> arr[2]));
        boolean[] res = new boolean[queries.length];
        int prev = 0;
        for (int[] query : queriesWithIdx){
            int costThreshold = query[2];
            for (int i = prev; i < edgeList.length; i++){
                if(edgeList[i][2] >= costThreshold){
                    //record the prev index , so that next run of kruskal should start from edge from that index
                    prev = i;
                    break;
                }
                int u = edgeList[i][0];
                int v = edgeList[i][1];
                union(find(hashMap.get(u)), find(hashMap.get(v)));
            }
            //after doing a kruskal on limites set of edges bound by constraint , check if the u and v are in same set or not
            if(find(hashMap.get(query[0])) == find(hashMap.get(query[1]))){
                res[query[3]] = true;
            }else
                res[query[3]] = false;
        }


        return  res;
    }
}
