func (t *unfn) find(n int) int {
	if n != t.g[n] {
		t.g[n] = t.find(t.g[n])
	}
	return t.g[n]
}

func (t *unfn) join(n1, n2 int) {
	g1, g2 := t.find(n1), t.find(n2)
	if g1 == g2 {
		return
	}
	if t.r[g1] > t.r[g2] {
		t.g[g2] = g1
	} else if t.r[g1] < t.r[g2] {
		t.g[g1] = g2
	} else {
		t.g[g1] = g2
		t.r[g2] += 1
	}
}

func (t *unfn) con(n1, n2 int) bool {
	g1, g2 := t.find(n1), t.find(n2)
	return g1 == g2
}

func newUn(n int) *unfn {
