class DisjointSet{
    public:
    vector<int>size;
    vector<int>head;
    DisjointSet(int n){
        size.resize(n,1);
        head.resize(n);
        for(int i=0;i<n;i++)head[i]=i;
    }

    int findUpar(int node){
        if(node==head[node])return node;

       return head[node]=findUpar(head[node]);
    }

    void UnionBySize(int u,int v){
        int jms=findUpar(u);
        int rms=findUpar(v);
        if(jms==rms)return ;
        if(size[jms]<size[rms]){
            head[jms]=rms;
            size[rms]+=size[jms];
        }
        else{
            head[rms]=jms;
            size[jms]+=size[rms];
        }

    }
};

class Solution {
public:
    static bool cmp(vector<int>&a,vector<int>&b){
        return a[2]<b[2];
    }
    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        DisjointSet ds(n);

        for(int i=0;i<queries.size();i++){
            queries[i].push_back(i);
        }
        vector<bool>ans(queries.size(),false);
        sort(queries.begin(),queries.end(),cmp);
        sort(edgeList.begin(),edgeList.end(),cmp);
        int j=0;
        for(int i=0;i<queries.size();i++){
            while(j<edgeList.size()&&edgeList[j][2]<queries[i][2]){
                ds.UnionBySize(edgeList[j][0],edgeList[j][1]);
                j++;
            }
            if(ds.findUpar(queries[i][0])==ds.findUpar(queries[i][1])){
                ans[queries[i][3]]=true;
            }
        }
        return ans;

    }
};



