* Sort queries based on limits .

* Sort edgeList based on weights.

* Find lower_bound of limit in edgeList . This indicates that upto `idx` index all the weighs in edgesList will be **strictly smaller** than `limit`.

* For all the connections in edgeList make components/sets using DSU. (DSU is the best data strcuture for this purpose).

* If Two elements come under a common set , then this shows that every edge in their path is strictly smalller than `limit`.




```
class DSU{
private:
    int n ; 
    vector<int> parent, rank ;

public:
    DSU(int n){
        this->n = n ;
        parent.resize(n);
        iota(begin(parent),end(parent),0);
        rank.resize(n,1) ;
    }

    int find_parent(int node){
        if(node == parent[node]) return node ;
        return parent[node] = find_parent(parent[node]) ;
    }
    void Union(int u , int v){
        int U = find_parent(u) , V = find_parent(v) ;
        if(U == V) return ;
        if(rank[U] < rank[V]) swap(U,V) ;
        rank[U] += rank[V] ;
        parent[V] = U ;
    }

};

struct cmp{
    bool operator()(const vector<int> &v1 , const vector<int> &v2){
        return v1[2] < v2[2] ;
    }
};

class Solution {
public:

    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        DSU dsu(n) ;
        vector<bool> ans(queries.size(),false) ;
        for(int i = 0 ; i < queries.size() ; ++i ) queries[i].push_back(i) ;

        sort(begin(queries),end(queries),[&](const vector<int> &v1 ,const vector<int> &v2)->bool{
           return v1[2] < v2[2] ; 
        });
        sort(begin(edgeList),end(edgeList),[&](const vector<int> &v1 , const vector<int> &v2)->bool{
           return v1[2] < v2[2] ; 
        });

        // for(auto &x : edgeList) cout << x[2] << " " ; cout << endl ;
        // for(auto &x : queries) cout << x[2] << " " ; cout << endl ;

        int prev = 0 ;
        for(auto &x : queries){
            int p = x[0] , q = x[1] , limit = x[2] , i = x[3] ;
			// to make sure comparision is done based on 2nd index in edgeList form a custom comparator cmp
            int idx = lower_bound(begin(edgeList) + prev , end(edgeList),vector<int>{INT_MIN,INT_MIN,limit},cmp()) - begin(edgeList) - 1 ;

            for(int j = prev ; j <= idx ; ++j ){
                //cout << j << endl ;
                int u = edgeList[j][0] , v = edgeList[j][1] , dis = edgeList[j][2] ;
                dsu.Union(u,v) ;
            }
            prev = idx + 1 ;
			//Check if p and q fall under the same set or not ?
            if(dsu.find_parent(p) == dsu.find_parent(q)) ans[i] = true ;
        }
        return ans ;
    }
};
