class Solution {
    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
        boolean[] output = new boolean[queries.length];

        Graph g = new Graph(n);

        for (int i=0; i<edgeList.length; i++) {
            g.addEdge(edgeList[i][0], edgeList[i][1], edgeList[i][2]);
        }

        for (int i=0; i<queries.length; i++) {
            int source = queries[i][0];
            int dest = queries[i][1];
            int limit = queries[i][2];
            boolean[] visited = new boolean[n];

            PriorityQueue<int[]> pq = new PriorityQueue<>((a, b) -> a[1] - b[1]);

            visited[source] = true;
            for (int j=0; j<g.arr.get(source).size(); j++) {
                int v = g.arr.get(source).get(j).v;
                int w = g.arr.get(source).get(j).weight;

                if (w < limit) {
                    pq.add(new int[]{v, w});
                }

            }

            pq.add(new int[]{source, 0});

            while (!pq.isEmpty()) {
                int[] current = pq.remove();

                int u = current[0];
                int dist = current[1];

                if (u == dest) {
                    output[i] = true;
                    break;
                }
                visited[u] = true;


               // System.out.println(u);
                for (int j=0; j<g.arr.get(u).size(); j++) {
                    int v = g.arr.get(u).get(j).v;
                    int w = g.arr.get(u).get(j).weight;



                    if (!visited[v] && w < limit) {
                       // System.out.println(v + " " + w);
                        pq.add(new int[]{v, w});
                    }
                } 
            }
        }

        return output;
    }
}

