            sortedQueries[i][1] = queries[i][1];
            sortedQueries[i][2] = queries[i][2];
            sortedQueries[i][3] = i;
        }

        Arrays.sort(sortedQueries, (a, b) -> a[2] - b[2]);

        int start = 0;
        boolean[] output = new boolean[queries.length];

        for (int i=0; i<sortedQueries.length; i++) {
            int[] query = sortedQueries[i];

            int w = query[2];

            while (start < edgeList.length && edgeList[start][2] < w) {
                uf.union(edgeList[start][0], edgeList[start][1]);
                start++;
            }

            if (uf.find(query[0]) == uf.find(query[1])) {
                output[query[3]] = true;
            }
        }

        return output;
    }
