        parent = new int[n];

        for (int i=0; i<n; i++) {
            parent[i] = i;
        }
    }

    public int find(int x) {
        while (x != parent[x]) {
            x = parent[x];
        }

        return x;
    }

    public void union(int x, int y) {
        int xParent = find(x);
        int yParent = find(y);

        parent[xParent] = yParent;
    }
}

