class Solution {
    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
        int queryLen = queries.length;
        int[][] queryWithIds = new int[queryLen][4];

        for (int i = 0; i < queryLen; i++) {
            queryWithIds[i] = new int[] {
              i, queries[i][0], queries[i][1], queries[i][2] 
            };
        }
        // sort quries by limit
        Arrays.parallelSort(queryWithIds, (r1, r2) -> Integer.compare(r1[3], r2[3]));
        // sort edges by dis
        Arrays.parallelSort(edgeList, (r1, r2) -> Integer.compare(r1[2], r2[2]));
        int edgeTop = 0;
        boolean[] result = new boolean[queryLen];
        // initialize UnionFind object
        UnionFind uf = new UnionFind(n);
        for (int i = 0; i < queryLen; i++) {
            int limit = queryWithIds[i][3];
            while (edgeTop < edgeList.length && edgeList[edgeTop][2] < limit) {
                uf.union(edgeList[edgeTop][0], edgeList[edgeTop][1]);
                edgeTop++;
            }
            result[queryWithIds[i][0]] = uf.find(queryWithIds[i][1]) == uf.find(queryWithIds[i][2]);
        }
        return result;
