void dfs(int u,int p){
    vis[u]=1;
    ances[u][0]=p;
    vector<int>tmp={p,u};
    dp[u][0]=cost[tmp];
    for(int i=1;i<18;i++){
        dp[u][i]=max(dp[ances[u][i-1]][i-1],dp[u][i-1]);
        ances[u][i]=ances[ances[u][i-1]][i-1];
    }
    h[u]=h[p]+1;
    for(int child:g[u]){
        if(vis[child])continue;
        dfs(child,u);
    }
}
int climb(int u,int ht,int &res){
    int bit=17;
    while(ht>0 and bit>=0){
        if((ht&(1<<bit))>0){
            ht-=(1<<bit);
            res=max(dp[u][bit],res);
            u=ances[u][bit];
        }
        bit--;
    }
    return u;
}
