    return par[u]=find(par[u]);
}
int lca(int u,int v,int& res){
    int res1=0;
    if(h[u]<h[v])swap(u,v);
    u=climb(u,h[u]-h[v],res1);
    res=max(res1,res);
    res1=0;
    if(u==v)return u;
    int bit=17;
    while(ances[u][0]!=ances[v][0] and bit>=0){
        if(ances[u][bit]==ances[v][bit]){
            bit--;
            continue;
        }
        res=max(dp[u][bit],res);
        res=max(dp[v][bit],res);
        u=ances[u][bit];
        v=ances[v][bit];
        bit--;
    }
    res=max(res,dp[u][0]);
    res=max(res,dp[v][0]);
    return ances[u][0];
}

