class Solution {
    private int[] f;

    private void init(int n) {
        f = new int[n];

        for (int i = 0; i < n; i++) {
            f[i] = i;
        }
    }

    private int find(int x) {
        if (f[x] == x) {
            return x;
        }

        return f[x] = find(f[x]);
    }

    private void union(int x, int y) {
        int p = find(x);
        int q = find(y);

        if (p != q) {
            f[p] = q;
        }
    }

    class Query implements Comparable<Query> {
        int[] qry;
        int index;

        public Query(int[] qry, int index) {
            this.qry = qry;
            this.index = index;
        }

        public int compareTo(Query q) {
            return this.qry[2] - q.qry[2];
        }
    }

    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
        if (edgeList == null || edgeList.length == 0 || queries == null || queries.length == 0) {
            return new boolean[0];
        }

        Arrays.sort(edgeList, (e1, e2) -> e1[2] - e2[2]);

        List<Query> list = new ArrayList();

        for (int i = 0; i < queries.length; i++) {
            list.add(new Query(queries[i], i));
        }

        Collections.sort(list);

        init(n);

        boolean[] res = new boolean[queries.length];

        int prevLimit = 0;
        int curLimit = 0;
        int prevIndex = 0;

        for (int i = 0; i < list.size(); i++) {
            Query q = list.get(i);

            curLimit = q.qry[2];

            // add new edges to union
            prevIndex = addEdges(prevIndex, prevLimit, curLimit, edgeList);

            // if two nodes has a valid path, put true
            if (find(q.qry[0]) == find(q.qry[1])) {
                res[q.index] = true;
            }

            prevLimit = curLimit;
        }


        return res;
    }

    // add new edges to uf
    private int addEdges(int prevIndex, int prevLimit, int curLimit, int[][] edges) {
        int i = prevIndex;

        for (i = prevIndex; i < edges.length; i++) {
            if (edges[i][2] >= prevLimit && edges[i][2] < curLimit) {
                union(edges[i][0], edges[i][1]);
            }
            else {
                break;
            }
        }

        return i;
    }
}

