Using DSU, we can add edges from small weight to large weight, and query from small limit to large limit. 

Time complexity:` O(E log E) + O(Q log Q)`
Space complexity: `O(n)`

```
    class DSU {
        vector<int> parent, rank;
        public:
        DSU(int n) {
            parent.resize(n);
            for (int x = 0; x < n; x++) {
                parent[x] = x;
            }
            rank.resize(n);
        }
        int find(int x) {
            int p = parent[x];
            if (p == x) return x;
            return parent[x] = find(p);
        }
        void unions(int x, int y) {
            x = find(x); y = find(y);
            if (x == y) return;

            if (rank[x] == rank[y]) rank[x]++;
            else if (rank[x] < rank[y]) swap(x, y);
            parent[y] = x;
        }
    };

public:
    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        auto cmp = [&](vector<int> &a, vector<int> &b) {
          return a[2] < b[2];  
        };
        sort(edgeList.begin(), edgeList.end(), cmp); // sort by weight

        for (int i = 0; i < queries.size(); i++) queries[i].push_back(i); // record index
        sort(queries.begin(), queries.end(), cmp);  // sort by limit

        DSU dsu(n);
        vector<bool> res(queries.size());
        int i = 0;
        for (auto &q : queries) {
            while(i < edgeList.size() && edgeList[i][2] < q[2]) { // only take edges with smaller weight than limit
                dsu.unions(edgeList[i][0], edgeList[i][1]);
                i++;
            }
            res[q[3]] = dsu.find(q[0]) == dsu.find(q[1]);
        }
        return res;
    }

