```python
class Union_Find(object):
    def __init__(self):
        self.group_id = 0
        self.groups = {}
        self.node_id = {}

    def union(self, a, b):
        A, B = a in self.node_id, b in self.node_id
        if A and B and self.node_id[a] != self.node_id[b]:
            self.merge(a, b)
        elif A or B:
            self.add(a, b)
        else:
            self.create(a, b)

    def merge(self, a, b):
        obs, targ = sorted((self.node_id[a], self.node_id[b]), key = lambda id: len(self.groups[id]))
        for node in self.groups[obs]:
            self.node_id[node] = targ
        self.groups[targ] |= self.groups[obs]
        del self.groups[obs]

    def add(self, a, b):
        a, b = (a, b) if a in self.node_id else (b, a)
        targ = self.node_id[a]
        self.node_id[b] = targ
        self.groups[targ] |= set([b])

    def create(self, a, b):
        self.groups[self.group_id] = set([a,b])
        self.node_id[a] = self.node_id[b] = self.group_id
        self.group_id += 1

    def is_connected(self, a, b):
        return a in self.node_id and b in self.node_id and self.node_id[a] == self.node_id[b]

class Solution:
    def distanceLimitedPathsExist(self, n: int, edges: List[List[int]], queries: List[List[int]]) -> List[bool]:

        res = [False] * len(queries)
        queries = [(i, q) for i,q in enumerate(queries)]
        queries.sort(key = lambda q: q[1][2])
        edges.sort(key = lambda e: e[2])

        uf = Union_Find()
        e = 0
        for i, (start, end, cost) in queries:
            # start and end are already connected - Query i is True
            if uf.is_connected(start, end):
                res[i] = True
                continue
            while e < len(edges):
                a, b, c = edges[e]
                if c >= cost:
                    # The cost to add an edge is too large - Query i is False
                    res[i] = False
                    break
                else:
                    # The cost to add an edge not too large - but start and end are not connected yet - keep adding edges
                    uf.union(a, b)
                    if uf.is_connected(start, end):
                        res[i] = True
                        break
                    e += 1
        return res
