class DSU{
public:
    vector<int> parent;
    vector<int> rank;
    int size;
    DSU(int n){
        this->size = n;
        parent.resize(n);
        rank.resize(n,1);
        for(int i=0;i<n;i++)   parent[i] = i;
    }
    int getParent(int node){
        if(node==parent[node])  return node;
        return parent[node] = getParent(parent[node]);
    }
    bool Union(int u,int v){
        int parentu = getParent(u);
        int parentv = getParent(v);
        if(parentu==parentv)    return false;

        if(rank[parentu]<rank[parentv])
            parent[parentu] = parentv ;
        else if(rank[parentv]<rank[parentu])
            parent[parentv] = parentu ;
        else{
            parent[parentu] = parentv ;
            rank[parentv]++;
        }
        return true;
    }   
};


class Solution {
public:
    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        vector<bool> result(queries.size(),false);

        vector<vector<int>> q;
        for(int i=0;i<queries.size();i++){
            queries[i].push_back(i);
        }
        // sorting the queries based on their limit value after storing their relative order(index)
        sort(queries.begin(),queries.end(),[](const vector<int> &a,const vector<int>& b){   
            return a[2]<=b[2];
        });

        // sorting the edges based on their distances
        sort(edgeList.begin(),edgeList.end(),[](const vector<int> &a,const vector<int> &b){
            return a[2]<b[2];
        });

        DSU d(n);

        int i = 0;
        for(auto &query:queries){
            int u = query[0];
            int v = query[1];
            int limit = query[2];
            int index = query[3];       

            bool ans = false;
            while(i<edgeList.size() && edgeList[i][2]<limit){       //building the graph until dis<limit is true
                int nodea = edgeList[i][0];
                int nodeb = edgeList[i][1];
                d.Union(nodea,nodeb);
                i++;
            }
            ans = d.getParent(u) == d.getParent(v) ;            // if both belongs to the same component means we can have path from u to v with each edge distance strictly less than limit
            result[index] = ans;
        }

        return result;
    }   
};
