class Solution {
public:

    static bool cmp(vector<int>& a,vector<int>& b) 
    {
        return (a[2]<b[2]);
    }

    int find(int x,vector<int>& par) 
    {
        if(x==par[x])
        return x;
        int y=find(par[x],par);
        return par[x]=y;
    }

    void merge(int x,int y,vector<int>& par,vector<int>& rank)
    {
        int lx=find(x,par),ly=find(y,par);
        if(lx!=ly)
        {
            if(rank[lx]>rank[ly])
            par[ly]=lx;
            else if(rank[ly]>rank[lx])
            par[lx]=ly;
            else
            {
                par[ly]=lx;
                rank[lx]++;
            }
        }
    }

    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) 
    { 
        int q=queries.size();
        int e=edgeList.size();
        for(int i=0;i<q;i++)
        queries[i].push_back(i);

        sort(edgeList.begin(),edgeList.end(),cmp); 
        sort(queries.begin(),queries.end(),cmp);
        //Sort edgeList and queries according to weights


        vector<int> par(n),rank(n);

        for(int i=0;i<n;i++)
        {
            par[i]=i;
            rank[i]=0;
        }

        vector<bool> out(q);

        int j=0;
        for(auto i:queries)
        {
            while(j<e&&edgeList[j][2]<i[2])
            {
                merge(edgeList[j][0],edgeList[j][1],par,rank);
                j++;
            }
            //Take all the edges with weight less than the query weight and merge them
            if(find(i[0],par)==find(i[1],par))
                out[i[3]]=1;
            else
                out[i[3]]=0;
            //Now if the two query nodes are merged, then a path with required constraints is possible
        }
        return out;
    }
};
