public:
    static bool cmp(vector<int> &a,vector<int> &b){
        return a[2]<b[2];
    }

    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& ed, vector<vector<int>>& q) {
        int n1=q.size(); 
        DisjointSet ds(n);
        // for storing ans
        for(int i=0; i<n1; i++){
            q[i].push_back(i);
        }

        sort(ed.begin(),ed.end(),cmp);
        sort(q.begin(),q.end(),cmp);

        vector<bool> ans(n1);
        int i=0;
        for(auto &it:q){
            while(i<ed.size() && ed[i][2]<it[2]){
             ds.unionByRank(ed[i][0],ed[i][1]);
             i++;
