# Approach

Sort `queries` by weight limit. Then for each weight limit increase, link nodes in the graph that are now connected.

# Code
```
#[derive(Debug)]
pub struct DisjointSet {
    parents: Vec<Option<usize>>,
    count: usize,
}

impl DisjointSet {
    pub fn new(n: usize) -> Self {
        let parents = vec![None; n];

        Self { parents, count: 0 }
    }

    pub fn init(&mut self, e: usize) {
        if e >= self.parents.len() {
            return;
        }
        if self.parents[e].is_some() {
            return;
        } // already initialized

        self.parents[e] = Some(e);
        self.count += 1; // new representative
    }

    pub fn find(&mut self, e: usize) -> Option<usize> {
        if e >= self.parents.len() {
            return None;
        }

        let parent = self.parents[e]?; // uninitialized
        if parent == e {
            return Some(e);
        } // self-representative

        let ret = self.find(parent)?;
        self.parents[e] = Some(ret); // path compression

        Some(ret)
    }

    pub fn link(&mut self, e0: usize, e1: usize) {
        if let Some(p0) = self.find(e0) {
            if let Some(p1) = self.find(e1) {
                if p0 == p1 {
                    return;
                } // already linked

                // p1 is no longer a representative
                self.parents[p1] = Some(p0);
                self.count -= 1;
            }
        }
    }

    pub fn len(&self) -> usize {
        self.count
    }
}

impl Solution {
    fn preproc(vec: Vec<Vec<i32>>) -> Vec<([usize; 2], u32)> {
        vec.into_iter()
            .filter(|v| v.len() > 2)
            .map(|v| {
                ([v[0] as usize, v[1] as usize], v[2] as u32)
            })
            .collect::<Vec<_>>()
    }

    pub fn distance_limited_paths_exist(
        n: i32,
        edge_vec: Vec<Vec<i32>>,
        queries: Vec<Vec<i32>>
    ) -> Vec<bool> {
        let n = n as usize;

        let mut edge_vec = Self::preproc(edge_vec);
        edge_vec.sort_unstable_by_key(|&(v, d)| (d, v));
        // println!("edge: {:?}", edge_vec);

        let queries = Self::preproc(queries);
        let n_queries = queries.len();
        let mut queries = queries.into_iter()
            .enumerate()
            .collect::<Vec<_>>();
        queries.sort_unstable_by_key(|&(i, (v, d))| ((d, v), i));
        // println!("queries: {:?}", queries);

        let mut set = DisjointSet::new(n);
        for i in 0..n { set.init(i); }

        let mut ret = vec![false; n_queries];

        let mut ptr_prev = usize::MIN;
        for (i, ([v0, v1], weight)) in queries {
            let ptr_1 = edge_vec.partition_point(|&(_, d)| d < weight);
            for ptr in ptr_prev..ptr_1 {
                let ([v0, v1], _) = edge_vec[ptr];
                set.link(v0, v1);
            }
            ptr_prev = ptr_1;

            if set.find(v0).unwrap() == set.find(v1).unwrap() {
                ret[i] = true;
            }
        }

        ret
    }
}
