class Solution {

    class DSU {
        int[] roots;
        int[] sizes;
        DSU(int size) {
            roots = new int[size];
            for (int i = 0; i < size; i++) {
                roots[i] = i;
            }
            sizes = new int[size];
        }
        public int find(int a) {
            if (roots[a] == a) {
                return a;
            }
            roots[a] = find(roots[a]);
            return roots[a];
        }

        public void union(int a, int b) {
            int rootA = find(a);
            int rootB = find(b);
            if (rootA == rootB) {
                return;
            }
            if (sizes[rootA] > sizes[rootB]) {
                roots[rootB] = rootA;
                sizes[rootA] += sizes[rootB];
            } else {
                roots[rootA] = rootB;
                sizes[rootB] += sizes[rootA];
            }
        }
    }

    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] queries) {
        boolean[] res = new boolean[queries.length];

        PriorityQueue<int[]> pq = new PriorityQueue<>((int[] v1, int[] v2)->Integer.compare(v1[2], v2[2]));
        for (int i = 0; i < queries.length; i++) {
            pq.add(new int[]{queries[i][0], queries[i][1], queries[i][2], i});
        }

        Arrays.sort(edgeList, (int[] v1, int[] v2)->Integer.compare(v1[2], v2[2]));

        DSU dsu = new DSU(n);

        int i = 0;
        while ( ! pq.isEmpty()) {
            int[] q = pq.poll();
            while (i < edgeList.length && edgeList[i][2] < q[2]) {
                dsu.union(edgeList[i][0], edgeList[i][1]);
                i++;
            }
            res[q[3]] = (dsu.find(q[0]) == dsu.find(q[1]));
        }

        return res;
    }
}
