    int findUPar(int node) {
        if (node == parent[node])
            return node;
        return parent[node] = findUPar(parent[node]);
    }

    void unionByRank(int u, int v) {
        int ulp_u = findUPar(u);
        int ulp_v = findUPar(v);
        if (ulp_u == ulp_v) return;
        if (rank[ulp_u] < rank[ulp_v]) {
            parent[ulp_u] = ulp_v;
        }
        else if (rank[ulp_v] < rank[ulp_u]) {
            parent[ulp_v] = ulp_u;
        }
        else {
            parent[ulp_v] = ulp_u;
            rank[ulp_u]++;
        }
    }

    void unionBySize(int u, int v) {
        int ulp_u = findUPar(u);
        int ulp_v = findUPar(v);
        if (ulp_u == ulp_v) return;
        if (size[ulp_u] < size[ulp_v]) {
            parent[ulp_u] = ulp_v;
            size[ulp_v] += size[ulp_u];
        }
        else {
            parent[ulp_v] = ulp_u;
            size[ulp_u] += size[ulp_v];
        }
    }
};
class Solution {
public:
    static bool com(vector<int>& a,vector<int>& b){
        return a[2]<b[2];
    }
    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        DisjointSet ds(n);
        int size=queries.size();
        vector<bool> Vec(size);
        map<pair<int,pair<int,int>>,vector<int>> mp;
        for(int i=0;i<queries.size();i++){
            mp[{queries[i][0],{queries[i][1],queries[i][2]}}].push_back(i);

        }
        sort(edgeList.begin(),edgeList.end(),com);
        sort(queries.begin(),queries.end(),com);
        int j=0;
        for(int i=0;i<queries.size();i++){
            while(j<edgeList.size() && edgeList[j][2]<queries[i][2]){
                ds.unionByRank(edgeList[j][0],edgeList[j][1]);
                j++;
            }
            int u=ds.findUPar(queries[i][0]);
            int v=ds.findUPar(queries[i][1]);
            pair<int,pair<int,int>> id={queries[i][0],{queries[i][1],queries[i][2]}};
            if(u==v){
                for(auto it:mp[id]){
                    Vec[it]=true;
                }
            }
        }
        return Vec;
    }
};
