class Dsu {
public:

    Dsu(int n) {
        root = rank = vector<int> (n, 0);
        for (int i = 0; i < n; i++) {
            root[i] = i;
        }
    }

    int find(int i) {
        return root[i] == i ? i : root[i] = find(root[i]);
    }

    void un(int i, int j) {
        int ri = find(i);
        int rj = find(j);
        if (ri == rj) return;
        if (rank[ri] > rank[rj]) root[rj] = ri;
        else if (rank[ri] < rank[rj]) root[ri] = rj;
        else {
            rank[ri]++;
            root[rj] = ri;
        }
    }

private:
    vector<int> root, rank;
};

class Solution {
public:
    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        auto comp = [](const auto& a, const auto& b) { return a[2] < b[2]; };
        for (int i = 0; i < queries.size(); i++) {
            queries[i].push_back(i);
        }
        sort(edgeList.begin(), edgeList.end(), comp);
        sort(queries.begin(), queries.end(), comp);
        int i = 0;
        vector<bool> ans (queries.size());
        Dsu dsu (n);
        for (auto q:queries) {
            while (i < edgeList.size() && edgeList[i][2] < q[2]) {
                dsu.un(edgeList[i][0], edgeList[i][1]);
                i++;
            }
            ans[q[3]] = dsu.find(q[0]) == dsu.find(q[1]);
        }
        return ans;
    }
};
