type UnionFind struct {
    rank []int
    group []int
}

func New(n int) *UnionFind {
    uf := &UnionFind{}
    uf.rank = make([]int, n)
    uf.group = make([]int, n)
    for i := 0; i < n; i++ {
        uf.rank[i] = 1
        uf.group[i] = i
    }
    return uf
}

func (uf *UnionFind) find(node int) int {
    if uf.group[node] == node {
        return node
    }
    uf.group[node] = uf.find(uf.group[node])
    return uf.group[node]
}

func (uf *UnionFind) join(node1, node2 int) int {
    node1, node2 = uf.find(node1), uf.find(node2)
    if node1 == node2 {
        return 0
    }
    if uf.rank[node1] > uf.rank[node2] {
        uf.group[node2] = node1
        uf.rank[node1] += uf.rank[node2]
    } else {
        uf.group[node1] = node2
        uf.rank[node2] += uf.rank[node1]
    }
    return 1
}

func (uf *UnionFind) areConnected(node1, node2 int) bool {
    return uf.find(node1) == uf.find(node2)
}

func distanceLimitedPathsExist(n int, edgeList [][]int, queries [][]int) []bool {
    uf := New(n)
    queriesCount := len(queries)
    answer := make([]bool, queriesCount)
    for i := range queries {
        queries[i] = append(queries[i], i)
    }
    sort.Slice(queries, func(i, j int) bool {
        return queries[i][2] < queries[j][2] 
    })
    edgesIndex := 0
    sort.Slice(edgeList, func(i, j int) bool {
        return edgeList[i][2] < edgeList[j][2]
    })
    for _, query := range queries {
        p, q, limit, queryOriginalIndex := query[0], query[1], query[2], query[3]
        for edgesIndex < len(edgeList) && edgeList[edgesIndex][2] < limit {
            uf.join(edgeList[edgesIndex][0], edgeList[edgesIndex][1])
            edgesIndex++
        }
        if uf.areConnected(p, q) {
            answer[queryOriginalIndex] = true
        }
    }
    return answer
}

