class DSU{
public:
    vector<int> rank, p;
    DSU(int n){
        rank.resize(n, 0);
        p.resize(n, 0);
        for(int i = 0;i < n;i++){
            p[i] = i;
        }
    }
    int Find(int x){
        if(p[x] != x){
            p[x] = Find(p[x]);
        }
        return p[x];
    }
    bool Union(int x, int y){
        int rootX = Find (x);
        int rootY = Find (y);
        if (rootX == rootY)
        {
            return false;
        }
        else
        {
            if (rank[rootX] > rank[rootY])
            {
                p[rootY] = rootX;
            }
            else if (rank[rootY] > rank[rootX])
            {
                p[rootX] = rootY;
            }
            else
            {
                p[rootX] = rootY;
                rank[rootY]++;
            }
        }
        return true;
    }
};

class Solution {
public:

    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& E, vector<vector<int>>& Q) {
        auto comp = [](auto a, auto b){
            return a[2] < b[2];
        };
        for(int i = 0;i < Q.size();i++){
            Q[i].push_back(i);
        }
        sort(E.begin(), E.end(), comp);
        sort(Q.begin(), Q.end(), comp);
        int idx = 0;
        vector<bool> res(Q.size(), 0);
        DSU ds(n);
        for(auto q : Q){
            int limit = q[2], a = q[0], b = q[1];
            while(idx < E.size() && E[idx][2] < limit){
                ds.Union(E[idx][0], E[idx][1]);
                idx++;
            }
            if((ds.Find(a) == ds.Find(b)))
                res[q[3]] = 1;
        }
        return res;
    }
};
