class Solution {
public:
    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edgeList, vector<vector<int>>& queries) {
        int max_group = 0;
        int m = queries.size();
        int e_size = edgeList.size();
        for(int i=0; i<m; i++)
        {   // adding index
            queries[i].push_back(i);
        }
        vector<bool> ans(m,false);
        vector<vector<int>> groups;
        vector<int> my_group(n,-1);
        auto cmp = [](vector<int> &a,vector<int> &b)->bool {return(a[2]<b[2]);};
        sort(queries.begin(),queries.end(),cmp);
        sort(edgeList.begin(),edgeList.end(),cmp);
        int next_edge  = 0;
        int last_limit = -1;
        for(int next_query = 0; next_query<m; next_query++)
        {
            int a = queries[next_query][0];
            int b = queries[next_query][1];
            if(last_limit == queries[next_query][2])
            {
                if (my_group[a] != -1 && my_group[a] == my_group[b])
                    ans[queries[next_query][3]] = true;
                else 
                    ans[queries[next_query][3]] = false; continue;
            }
            last_limit = queries[next_query][2];
