            {
                int ae = edgeList[next_edge][0];
                int be = edgeList[next_edge][1];
                if (my_group[ae] == -1)
                {
                    if (my_group[be] == -1)
                    {
                        groups.push_back({ae,be});
                        my_group[be] = max_group++;
                    }
                    else
                    {
                        groups[my_group[be]].push_back(ae);
                    }
                    my_group[ae] = my_group[be];
                }
                else if (my_group[be] == -1)
                {
                    groups[my_group[ae]].push_back(be);
                    my_group[be] = my_group[ae];
                }
                else if  (my_group[be] != my_group[ae])
                {  // merge groups
                    if (groups[my_group[be]].size() > groups[my_group[ae]].size()) swap(ae,be);
                    int tmp = my_group[be];
                    for(auto i : groups[tmp])
                    {
                         groups[my_group[ae]].push_back(i);
                        my_group[i] = my_group[ae];
