            return parent[node] = findParent(parent[node]);
        }

        void makeUnion(int node1,int node2){
            int u = findParent(node1),
            v = findParent(node2);

            if(u == v) return;
            if(size[u] > size[v]){
                parent[v] = u;
                size[v] += size[u];
            }
            else{
                parent[u] = v;
                size[u] += size[v];
