class Solution {
    fun distanceLimitedPathsExist(n: Int, edgeList: Array<IntArray>, queries: Array<IntArray>): BooleanArray {
        edgeList.sortBy { it[2] }
        val uf = UnionFind(n)
        val results = BooleanArray(queries.size)
        val queryIndices = queries.indices.sortedBy { queries[it][2] }

        var edgeIndex = 0

        for (queryIndex in queryIndices) {
            val (p, q, limit) = queries[queryIndex]

            while (edgeIndex < edgeList.size && edgeList[edgeIndex][2] < limit) {
                uf.union(edgeList[edgeIndex][0], edgeList[edgeIndex][1])
                edgeIndex++
            }

            results[queryIndex] = uf.find(p) == uf.find(q)
        }

        return results
    }
}

class UnionFind(n: Int) {
    private val parent = IntArray(n) { it }
    private val rank = IntArray(n) { 0 }

    fun find(x: Int): Int {
        if (parent[x] != x) {
            parent[x] = find(parent[x])
        }
        return parent[x]
    }

    fun union(x: Int, y: Int) {
        val rootX = find(x)
        val rootY = find(y)

        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                parent[rootY] = rootX
            } else {
                parent[rootX] = rootY
                if (rank[rootX] == rank[rootY]) {
                    rank[rootY]++
                }
            }
        }
    }
}
