class Solution {
    public boolean[] distanceLimitedPathsExist(int n, int[][] edgeList, int[][] ques) {
        DSU dsu = new DSU(n);
        boolean ans[]=new boolean[ques.length];
        int q[][]=new int[ques.length][4];
        for(int i=0;i<ques.length;i++){
            q[i][0]=ques[i][0];
            q[i][1]=ques[i][1];
            q[i][2]=ques[i][2];
            q[i][3]=i;
        }
        sort(q);
        sort(edgeList);
        int j=0;
        for(int i=0;i<ques.length;i++){
            int d=q[i][2];
            for(;j<edgeList.length;j++){
                if(edgeList[j][2]<d){
                    int n1=edgeList[j][0];
                    int n2=edgeList[j][1];
                    dsu.join(n1,n2);
                }
                else{
                    break;
                }
            }
            boolean temp=dsu.isPair(q[i][0],q[i][1]);
            if(temp){
                ans[q[i][3]]=true;   
            }
        }
        return ans;
    }

    public void sort(int arr[][]){
        Arrays.sort(arr,new Comparator<int[]>(){
        public int compare(int a[], int b[]){
            return a[2]-b[2];
        }
        });
    }

