class UnionFind {
  constructor(size) {
    this.group = [];
    this.rank = [];
    for (let i = 0; i < size; ++i) {
      this.group[i] = i;
    }
  }

  find(node) {
    if (this.group[node] !== node) {
      this.group[node] = this.find(this.group[node]);
    }
    return this.group[node];
  }

  join(node1, node2) {
    let group1 = this.find(node1);
    let group2 = this.find(node2);

    // node1 and node2 already belong to same group.
    if (group1 === group2) {
      return;
    }

    if (this.rank[group1] > this.rank[group2]) {
      this.group[group2] = group1;
    } else if (this.rank[group1] < this.rank[group2]) {
      this.group[group1] = group2;
    } else {
      this.group[group1] = group2;
      this.rank[group2] += 1;
    }
  }

  areConnected(node1, node2) {
    let group1 = this.find(node1);
    let group2 = this.find(node2);
    return group1 === group2;
  }
}

var distanceLimitedPathsExist = function (n, edgeList, queries) {
  const uf = new UnionFind(n);

  for (let i = 0; i < queries.length; i++) {
    queries[i].push(i);
  }

  const sorter = (a, b) => {
    return a[2] - b[2];
  }

  edgeList.sort(sorter);
  queries.sort(sorter);
  const result = new Array(queries.length);
  let j = 0;
  for (let i = 0; i < queries.length; i++) {
    const [u, v, t, idx] = queries[i];
    while (j < edgeList.length && edgeList[j][2] < t) {
      uf.join(edgeList[j][0], edgeList[j][1]);
      j++;
    }
    if (uf.find(u) == uf.find(v)) {
      result[idx] = true;
    } else result[idx] = false;
  }
  return result;
};
