var distanceLimitedPathsExist = function(n, edgeList, queries) {
    let queriesMapIndex = new Map();
    for (let index = 0; index < queries.length; index += 1) {
        let [n1, n2, maxDist] = queries[index];
        if (queriesMapIndex.has(n1 + '-' + n2 + '-' + maxDist) === false) {
            queriesMapIndex.set(n1 + '-' + n2 + '-' + maxDist, []);
        }
        queriesMapIndex.get(n1 + '-' + n2 + '-' + maxDist).push(index);
    }

    queries.sort((a, b) => a[2] - b[2]);
    edgeList.sort((a, b) => a[2] - b[2]);

    let parent = [...Array(n).keys()];
    let rank = [...Array(n).fill(1)];

    let find = function(node) {
        while (node != parent[node]) {
            parent[node] = parent[parent[node]];
            node = parent[node];
        }
        return node;
    }

    let union = function(n1, n2) {
        let p1 = find(n1);
        let p2 = find(n2);

        if (rank[p1] > rank[p2]) {
            [p1, p2] = [p2, p1]; // p2 is always greater size
        }

        parent[p1] = p2;
        rank[p2] += 1;
    }

    let result = [...Array(queries.length)];
    let prevIndex = 0;
    for (let [n1, n2, maxDist] of queries) {
        for (let index = prevIndex; index < edgeList.length; index += 1) {
            let [node, neighbor, dist] = edgeList[index];
            if (dist >= maxDist) {
                prevIndex = index;
                break;
            }
            union(node, neighbor);
        }
        parent[n1] = find(n1);
        parent[n2] = find(n2);
        for (let index of queriesMapIndex.get(n1 + '-' + n2 + '-' + maxDist)) {
            result[index] = parent[n1] == parent[n2];
        }
    }

    return result;
};
