class DSU {
    int par[], size[];

    DSU(int n) {
        par = new int[n+1];
        size = new int[n+1];
        Arrays.fill(par, -1);
        Arrays.fill(size, 1);
    }

    int findPar(int u) {
        return par[u] == -1 ? u : (par[u] = findPar(par[u]));
    }

    boolean unite(int u, int v) {
        u = findPar(u);
        v = findPar(v);
        if (u == v) return false;
        if (size[u] > size[v]) u = u ^ v ^ (v = u); // swap u and v
        par[u] = v;
        size[v] += size[u];
        return true;
    }
}



class Solution {
    public boolean[] distanceLimitedPathsExist(int n, int[][] el, int[][] qs) {
        int len = qs.length;
        boolean ans[] = new boolean[len];
        DSU dsu = new DSU(n);

        Comparator<int[]> cmp = (a, b) -> a[2] - b[2];
        BiFunction<Integer, Integer, Boolean> isInSameGrp = (u, v) -> dsu.findPar(u) == dsu.findPar(v);

        for (int i = 0; i < len; i++){
            int tmp[] = new int[4];
            tmp[0] = qs[i][0];
            tmp[1] = qs[i][1];
            tmp[2] = qs[i][2];
            tmp[3] = i;
            qs[i] = tmp;
        }
        Arrays.sort(el, cmp);
        Arrays.sort(qs, cmp);

        for (int i = 0, j = 0; i < qs.length; i++) {
            while (j < el.length && el[j][2] < qs[i][2]){
                dsu.unite(el[j][0], el[j][1]); 
                j++;
            }
            ans[qs[i][3]] = isInSameGrp.apply(qs[i][0], qs[i][1]);
        }
        return ans;
    }
}
