        for(int i=0;i<q.size();i++){
            v.push_back({q[i][2],i});
        }


        sort(v.begin(),v.end());


        int ptr=0;

        for(int i=0;i<v.size();i++){

            while(ptr<edge.size() and edge[ptr][0]<v[i].first){

                int u=edge[ptr][1];
                int v=edge[ptr][2];

                int parent_u=find(u,parent);
                int parent_v=find(v,parent);

                if(parent_u!=parent_v){
                    parent[parent_u]=parent_v;
                }

                ptr++;

            }

            int x=q[v[i].second][0];
            int y=q[v[i].second][1];

            int px=find(x,parent);
            int py=find(y,parent);

            if(px==py){

                res[v[i].second]=true;
            }
        }


     return res;


    }
