impl Solution {
    pub fn distance_limited_paths_exist(n: i32, mut edge_list: Vec<Vec<i32>>, mut queries: Vec<Vec<i32>>) -> Vec<bool> {
        let qlen = queries.len();
        let mut uf = UnionFind::new(n as usize);
        let mut answer = vec![false; qlen];

        for i in 0..qlen {
            queries[i].push(i as i32);
        }

        edge_list.sort_by(|a, b| a[2].cmp(&b[2]));
        queries.sort_by(|a, b| a[2].cmp(&b[2]));

        let mut edge_index = 0;
        for i in 0..qlen {
            let (p, q, limit, original_index) = (queries[i][0], queries[i][1], queries[i][2], queries[i][3]);

            while edge_index < edge_list.len() && edge_list[edge_index][2] < limit {
                let (node1, node2) = (edge_list[edge_index][0], edge_list[edge_index][1]);
                uf.set(node1 as usize, node2 as usize);
                edge_index += 1;
            }

            answer[original_index as usize] = uf.connected(p as usize, q as usize);
        }

        answer
    }
}

struct UnionFind {
    group: Vec<usize>,
}

impl UnionFind {
    fn new(len: usize) -> Self {
        Self {
            group: (0..len).collect()
        }
    }

    fn find(&mut self, i: usize) -> usize {
        if self.group[i] != i {
            self.group[i] = self.find(self.group[i]);
        }

        self.group[i]
    }

    fn set(&mut self, i: usize, j: usize) {
        let g1 = self.find(i);
        let g2 = self.find(j);
        self.group[g1] = self.group[g2];
    }

    fn connected(&mut self, i: usize, j: usize) -> bool {
        self.find(i) == self.find(j)
    }
}
