class Solution {
public:
    static bool cmp(vector<int> &a, vector<int> &b) {
        return a[2] < b[2];
    }

    vector<bool> distanceLimitedPathsExist(int n, vector<vector<int>>& edges, vector<vector<int>>& queries) {

        // Step 1: Add original index to the every query, so that the order in final
        //         answer can be maintained.
        for(int i=0; i < queries.size(); i++)
            queries[i].push_back(i);

        // Step 2: sort queries in increasing order according to the distance.
        // sorting every row of matrix.
        sort(queries.begin(), queries.end(), cmp);

        // Step 3: Take a query, pick its limitDist, and start making graph by disjointSetUnion
        // till we are getting edges with (wt < limitDist).
        // The check for the existence of required edges.


        // to get edges having smaller distance first.
        sort(edges.begin(), edges.end(), cmp);

        int i = 0;
        DisjointSet ds(n);
        vector<bool> ans(queries.size(), false);

        for(auto q: queries) {
            int reqU = q[0];
            int reqV = q[1];
            int limit = q[2];
            int original_index = q[3];

