class Solution {
public:
    // creating the letter pattern that will help us to map the va
    vector<vector<int>> keys = {
                                { 'a','b','c' }, { 'd','e','f' }, { 'g','h','i' }, { 'j','k','l' }, 
                                { 'm','n','o' }, { 'p','q','r','s' }, { 't','u','v' }, { 'w','x','y','z' }
                               };

    // it will store the final answer
    vector<string> ans;

    void helper(string digits,vector<string> &ans,int start,int end,string tmp)
    {
        // if our start reaches to the bound
        if(start>=end)
        {
            ans.push_back(tmp);
            return;
        }

        // check for the other associative chars with their respective integers
        // here we use keys[digits[i]-'2'] , because we made our keys that start with idx 0 and if we see digit pattern then 
        // observe that it actuallt start from 2 right so for balancing that we use digits[i]-'2'
        for(char x : keys[digits[start]-'2'])
        {
            // call the same helper fundtion by insreasing start count by 1 because we have done woth current now go to second one
            // and add current char to tmp because we took the current char and check for its other associatives
            helper(digits,ans,start+1,digits.size(),tmp+x);
        }
    }

    vector<string> letterCombinations(string digits) 
    {
        // if our string is initially empty
        if(digits=="")
            return ans;

        // else call the helper function that will gave all the combinations
        helper(digits,ans,0,digits.size(),"");
        return ans;
    }
};
