        if(temp.size()==digits.size())
        {
            ans.push_back(temp);
            return;
        }
        for(auto i : v[digits[start]-'0'])
        {
            temp.push_back(i);
            generate(digits,temp,start+1,ans);
            temp.pop_back();
        }
