class Solution:
    def letterCombinations(self, digits: str) -> List[str]:
        ans = []
        dial = ["0", "1", "abc", "def", "ghi", "jkl", "mno", "pqrs", "tuv", "wxyz"]

        def helper(comb, index, digits):
            nonlocal ans
            if index == len(digits):
                ans.append(comb)
                return

            letters = dial[int(digits[index])]
            for char in letters:
                helper(comb + char, index + 1, digits)

        if len(digits) == 0:
            return ans

        helper("", 0, digits)
        return ans

